const cacheKeyCustomWordCardData = "customWordCardData"

// 展示页面内通知和系统通知
const sendNotification = (item) => {
  $message.success(`${item.learningLanguage} ${item.fromLanguage}`)

  // 创建系统通知
  $chrome.notifications.create(
    null,
    {
      type: "basic",
      title: item.learningLanguage,
      message: item.fromLanguage,
      iconUrl:
        "https://d35aaqx5ub95lt.cloudfront.net/images/pathCharacters/guidebook/939ebad747027f1a9d365e15fda3a4e1.svg",
    },
    $chrome.proxy((notificationId) => {
