const cacheKeyCustomWordCardData = "customWordCardData"

// 展示页面内通知和系统通知
const sendNotification = (item) => {
  $message.success(`${item.learningLanguage} ${item.fromLanguage}`)

  // 创建系统通知
  $chrome.notifications.create(
    null,
    {
      type: "basic",
      title: item.learningLanguage,
      message: item.fromLanguage,
      iconUrl:
        "https://d35aaqx5ub95lt.cloudfront.net/images/pathCharacters/guidebook/939ebad747027f1a9d365e15fda3a4e1.svg",
    },
    $chrome.proxy((notificationId) => {
      // 延迟 6 秒清除系统通知
      setTimeout(() => {
        $chrome.notifications.clear(notificationId)
      }, 6000)
    })
  )
}

// 添加或修改自定义单词
$self.addOrUpdateCustomWord = async (data) => {
  const list = [...$self.value]

  if (data.id) {
    // 修改
    const index = list.findIndex((item) => item.id === data.id)
    list[index] = data
  } else {
    // 添加
    data.id = Date.now()
    list.push(data)
  }

  try {
    await $chrome.localStorage.setItem(
      cacheKeyCustomWordCardData,
      JSON.stringify(list)
    )

    $self.refreshCustomWordTable()
  } catch (e) {
    $logger.info(`${data.id ? "修改" : "添加"}自定义单词数据失败`, e)
  }
}

// 获取自定义单词列表
$self.fetchCustomWordList = async () => {
  let res = null
  try {
    res = await $chrome.localStorage.getItem(cacheKeyCustomWordCardData)
  } catch (e) {
    $logger.info("获取自定义单词列表失败", e)
    return []
  }

  if (!res) {
    return []
  }

  try {
    const customWordCardData = JSON.parse(res) || []
    return customWordCardData
  } catch (e) {
    $logger.info("解析自定义单词列表数据失败", e)
    return []
  }
}

// 保存自定义单词列表数据
$self.saveCustomWordList = async () => {
  const list = $self.value
  try {
    await $chrome.localStorage.setItem(
      cacheKeyCustomWordCardData,
      JSON.stringify(list)
    )
  } catch (e) {
    $logger.info(`保存自定义单词列表数据失败`, e)
  }
}

// 刷新自定义单词列表
$self.refreshCustomWordTable = async () => {
  const customWordCardData = await $self.fetchCustomWordList()
  $self.value = customWordCardData

  if (customWordCardData.length > 0) {
    const randomIndex = parseInt(Math.random() * customWordCardData.length)
    const randomItem = customWordCardData[randomIndex]
    sendNotification(randomItem)
  }
}

