    $self
      .validate() // 校验
      .then(async () => {
        const customWordForm = $self.value
        $logger.info("$self.value", customWordForm)

        $self
          .query("customWordTable")
          .take()
          .addOrUpdateCustomWord({
            ...customWordForm,
          })

        // 关闭当前对话框
        $self.componentProps.open = false
      })
      .catch((e) => {
        $logger.info("表单校验失败", e)
      })
  },
})

