fulfill.run:
$props({
  onClick: () => {
    window.currentForm = $form
    window.currentField = $self

    // 获取弹窗
    const dialog = $self.query("updateCurrentWindowDialog").take()
    // 打开弹窗
    dialog.componentProps.open = true
  },
})

$props({
  // 点击取消按钮
  onCancel: () => {
    // 关闭当前对话框
    $self.componentProps.open = false
  },
  // 点击确认按钮
  onOk: () => {
    $self
      .validate() // 校验
      .then(async () => {
        // $logger.info("$self.value", $self.value)

        // 关闭当前对话框
        $self.componentProps.open = false

        // 获取当前窗口
        // const currentWindow = await $chrome.windows.getCurrent()
        const currentWindow = await $chrome.callMethodByPath(
          "windows.getCurrent"
        )

        // 更新当前窗口尺寸
        // $chrome.windows.update(currentWindow.id, {
        //   ...$self.value,
        // })
        $chrome.callMethodByPath("windows.update", currentWindow.id, {
          ...$self.value,
        })
      })
      .catch((e) => {
        $logger.info("表单校验失败", e)
      })
  },
})
