const rootEntire = $self.address.entire
// 书签列表持久化缓存的 key
const cacheKeyBookmarkList = `${rootEntire}BookmarkList`
// 书签样式持久化缓存的 key
const cacheKeyBookmarkStyle = `${rootEntire}BookmarkStyle`
// 默认书签样式
const defaultBookmarkStyle = "verticalImgAndText"

$self.state = $observable({
  bookmarkStyle: "",
})

// 默认书签列表
const defualtList = [
  {
    id: Date.now(),
    title: "React",
    url: "https://zh-hans.react.dev/learn",
  },
  {
    id: Date.now() + 1,
    title: "Vue",
    url: "https://cn.vuejs.org/v2/guide/index.html",
  },
]

// 获取书签样式
$self.getBookmarkStyle = async () => {
  if ($self.state.bookmarkStyle) {
    // 返回内存缓存的书签样式
    return $self.state.bookmarkStyle
  }

  let res = null
  try {
    res = await $chrome.localStorage.getItem(cacheKeyBookmarkStyle)
  } catch (e) {
    $logger.info("获取书签样式失败", e)
  }

  $self.state.bookmarkStyle = res || defaultBookmarkStyle
  return $self.state.bookmarkStyle
}

// 修改书签样式
$self.updateBookmarkStyle = async (bookmarkStyle) => {
  try {
    await $chrome.localStorage.setItem(cacheKeyBookmarkStyle, bookmarkStyle)

    $self.state.bookmarkStyle = bookmarkStyle

    // $self.refreshBookmarkListAndTable()
  } catch (e) {
    $logger.info("修改书签数据失败", e)
  }
}

// 展示书签管理对话框
$self.showBookmarkManageDialog = () => {
  const bookmarkManageDialog = $self
    .query(`${rootEntire}.bookmarkManageDialog`)
    .take()
  bookmarkManageDialog.setComponentProps({
    open: true,
  })
}

// 展示添加或书签对话框
$self.showAddOrUpdateBookmarkDialog = (bookmark) => {
  const addBookmarkDialog = $self
    .query(`${rootEntire}.addBookmarkDialog`)
    .take()
  addBookmarkDialog.value = {
    ...(bookmark || {}),
  }
  addBookmarkDialog.setComponentProps({
    open: true,
    title: `${bookmark ? "修改" : "添加"}书签`,
  })
}

// 添加或修改书签
$self.addOrUpdateBookmark = async (bookmark) => {
  const list = await $self.fetchBookmarkList()

  if (bookmark.id) {
    // 修改
    const index = list.findIndex((item) => item.id === bookmark.id)
    list[index] = bookmark
  } else {
    // 添加
    bookmark.id = Date.now()
    list.push(bookmark)
  }

  try {
    await $chrome.localStorage.setItem(
      cacheKeyBookmarkList,
      JSON.stringify(list)
    )

    $self.refreshBookmarkListAndTable()
  } catch (e) {
    $logger.info(`${bookmark.id ? "修改" : "添加"}书签数据失败`, e)
  }
}

// 删除书签
$self.deleteBookmark = async (bookmark) => {
  let list = await $self.fetchBookmarkList()
  list = list.filter((item) => item.id !== bookmark.id)
  $self.saveBookmarkList(list)
}

// 往前移动书签
$self.moveUpBookmark = async (bookmark) => {
  let list = await $self.fetchBookmarkList()

  const index = list.findIndex((item) => item.id === bookmark.id)
  let preIndex = index - 1
  if (preIndex === -1) {
    // 已经是第一条，则和最后一条互换
    preIndex = list.length - 1
  }

  const other = list[preIndex]
  list[preIndex] = bookmark
  list[index] = other
  $self.saveBookmarkList(list)
}

// 往后移动书签
$self.moveDownBookmark = async (bookmark) => {
  let list = await $self.fetchBookmarkList()

  const index = list.findIndex((item) => item.id === bookmark.id)
  let nextIndex = index + 1
  if (nextIndex === list.length) {
    // 已经是最后一条，则和第一条互换
    nextIndex = 0
  }

  const other = list[nextIndex]
  list[nextIndex] = bookmark
  list[index] = other

  $self.saveBookmarkList(list)
}

// 保存书签列表数据
$self.saveBookmarkList = async (bookmarkList) => {
  try {
    await $chrome.localStorage.setItem(
      cacheKeyBookmarkList,
      JSON.stringify(bookmarkList)
    )

    $self.refreshBookmarkListAndTable()
  } catch (e) {
    $logger.info(`保存书签列表数据失败`, e)
  }
}

// 获取书签列表
$self.fetchBookmarkList = async () => {
  let res = null
  try {
    res = await $chrome.localStorage.getItem(cacheKeyBookmarkList)
  } catch (e) {
    $logger.info("获取书签列表失败", e)
    return []
  }

