// 书签列表持久化缓存的 key
const cacheKeyBookmarkList = `${rootEntire}BookmarkList`
// 书签样式持久化缓存的 key
const cacheKeyBookmarkStyle = `${rootEntire}BookmarkStyle`
// 默认书签样式
const defaultBookmarkStyle = "verticalImgAndText"

