// 添加或修改书签
$self.addOrUpdateBookmark = async (bookmark) => {
  const list = await $self.fetchBookmarkList()

  if (bookmark.id) {
    // 修改
    const index = list.findIndex((item) => item.id === bookmark.id)
    list[index] = bookmark
  } else {
    // 添加
    bookmark.id = Date.now()
    list.push(bookmark)
  }

  try {
    await $chrome.localStorage.setItem(
      cacheKeyBookmarkList,
      JSON.stringify(list)
    )

    $self.refreshBookmarkListAndTable()
  } catch (e) {
    $logger.info(`${bookmark.id ? "修改" : "添加"}书签数据失败`, e)
  }
}

// 删除书签
$self.deleteBookmark = async (bookmark) => {
  let list = await $self.fetchBookmarkList()
  list = list.filter((item) => item.id !== bookmark.id)
  $self.saveBookmarkList(list)
}

// 往前移动书签
$self.moveUpBookmark = async (bookmark) => {
  let list = await $self.fetchBookmarkList()

  const index = list.findIndex((item) => item.id === bookmark.id)
  let preIndex = index - 1
  if (preIndex === -1) {
    // 已经是第一条，则和最后一条互换
    preIndex = list.length - 1
  }

  const other = list[preIndex]
  list[preIndex] = bookmark
  list[index] = other
  $self.saveBookmarkList(list)
}

// 往后移动书签
$self.moveDownBookmark = async (bookmark) => {
  let list = await $self.fetchBookmarkList()

  const index = list.findIndex((item) => item.id === bookmark.id)
  let nextIndex = index + 1
  if (nextIndex === list.length) {
    // 已经是最后一条，则和第一条互换
    nextIndex = 0
  }

  const other = list[nextIndex]
  list[nextIndex] = bookmark
  list[index] = other

  $self.saveBookmarkList(list)
}

// 保存书签列表数据
$self.saveBookmarkList = async (bookmarkList) => {
  try {
    await $chrome.localStorage.setItem(
      cacheKeyBookmarkList,
      JSON.stringify(bookmarkList)
    )

    $self.refreshBookmarkListAndTable()
  } catch (e) {
    $logger.info(`保存书签列表数据失败`, e)
  }
}

// 获取书签列表
$self.fetchBookmarkList = async () => {
  let res = null
  try {
    res = await $chrome.localStorage.getItem(cacheKeyBookmarkList)
  } catch (e) {
    $logger.info("获取书签列表失败", e)
    return []
  }

  if (!res) {
    $self.saveBookmarkList(defualtList)
    return defualtList
  }

  try {
    const bookmarkCardData = JSON.parse(res) || []
    console.log("bookmarkCardData", bookmarkCardData)
    return bookmarkCardData
  } catch (e) {
    $logger.info("解析书签列表数据失败", e)
    return []
  }
}

// 刷新书签列表和管理表格
$self.refreshBookmarkListAndTable = async () => {
  const bookmarkCardData = await $self.fetchBookmarkList()

  const bookmarkIteratorLayout = $self
    .query(`${rootEntire}.bookmarkIteratorLayout`)
    .take()
  if (bookmarkIteratorLayout) {
    // 先清空再重新赋值，负责书签图标不会更新
    bookmarkIteratorLayout.value = []
    bookmarkIteratorLayout.value = bookmarkCardData
  }
  window.bookmarkIteratorLayout = bookmarkIteratorLayout

  const bookmarkTable = $self.query(`${rootEntire}.*.bookmarkTable`).take()
  if (bookmarkTable) {
    bookmarkTable.value = bookmarkCardData
  }
}

